mapper.select:
        SELECT
            bu.bu_name AS buName,
            whd.warehouse_name AS warehouseName,
            im.item_name AS itemName,
            im.spec,
            im.base_unit AS baseUnit,
            im.big_category AS itemBigCategory,
            im.mid_category AS itemMidCategory,
            im.small_category AS itemSmallCategory,
            im.asset_class AS assetClass,

            (
                SELECT NVL(SUM(qty), 0)
                FROM inbound_detail
                WHERE item_id = im.item_id AND created_at < #{startDate, jdbcType=DATE}
            )
            -
            (
                SELECT NVL(SUM(qty), 0)
                FROM out_detail
                WHERE item_id = im.item_id AND created_at < #{startDate, jdbcType=DATE}
            ) AS beginningStock,

            (
                SELECT NVL(SUM(qty), 0)
                FROM inbound_detail
                WHERE item_id = im.item_id AND created_at BETWEEN #{startDate, jdbcType=DATE} AND #{endDate, jdbcType=DATE}
            ) AS inboundQty,

            (
                SELECT NVL(SUM(qty), 0)
                FROM out_detail
                WHERE item_id = im.item_id AND created_at BETWEEN #{startDate, jdbcType=DATE} AND #{endDate, jdbcType=DATE}
            ) AS outQty,

            (
                SELECT NVL(SUM(qty), 0)
                FROM inbound_detail
                WHERE item_id = im.item_id AND created_at <= #{endDate, jdbcType=DATE}
            )
            -
            (
                SELECT NVL(SUM(qty), 0)
                FROM out_detail
                WHERE item_id = im.item_id AND created_at <= #{endDate, jdbcType=DATE}
            ) AS endingStock

        FROM item_master im
        LEFT JOIN business_unit bu ON im.bu_id = bu.bu_id
        LEFT JOIN warehouse_item wi ON im.item_id = wi.item_id
        LEFT JOIN warehouse_detail whd ON wi.warehouse_id = whd.warehouse_id
        LEFT JOIN warehouse_master whm ON whd.warehouse_master_id = whm.warehouse_master_id

        WHERE 1=1
        
            AND im.bu_id = #{buId}
        
        
            AND whd.warehouse_id = #{warehouseId}
        
        
            AND im.big_category = #{itemBigCategory}
        
        
            AND im.mid_category = #{itemMidCategory}
        
        
            AND im.small_category = #{itemSmallCategory}
        
        
            AND im.item_name LIKE '%' || #{itemName} || '%'
        
        
            AND im.spec LIKE '%' || #{spec} || '%'
        
        
            AND im.base_unit = #{baseUnit}
        
        
            AND im.importance_level = #{importanceLevel}
        
        
            AND im.asset_class = #{itemAssetClass}
        
        GROUP BY bu.bu_name, whd.warehouse_name, im.item_name, im.spec, im.base_unit, im.big_category, im.mid_category, im.small_category, im.asset_class, im.item_id
